from unittest import TestCase

from generator_domain.thumbnailer import Thumbnailer


image_b64 = ""
image_large_b64 = ""


class TestThumbnailer(TestCase):

    def setUp(self):
        self.thumbnailer = Thumbnailer()

    def test_should_return_unchanged_data_if_image_size_is_minimum(self):
        image_data = {
            "width": 200,
            "height": 200,
            "base64": image_b64
        }

        processed_data = self.thumbnailer._make_thumbnails(image_data)

        self.assertEqual(processed_data, image_data)

    def test_should_return_thumbnails_if_image_larger(self):
        image_data = {
            "width": 800,
            "height": 800,
            "base64": image_large_b64
        }

        processed_data = self.thumbnailer._make_thumbnails(image_data)

        self.assertTrue("base64_600_thumb" in processed_data.keys())
        self.assertTrue("base64_200_thumb" in processed_data.keys())
